      out.write("\t\t// 对Date的扩展，将 Date 转化为指定格式的String\r\n");
      out.write("\t\t// 月(M)、日(d)、小时(h)、分(m)、秒(s)、季度(q) 可以用 1-2 个占位符，\r\n");
      out.write("\t\t// 年(y)可以用 1-4 个占位符，毫秒(S)只能用 1 个占位符(是 1-3 位的数字)\r\n");
      out.write("\t\t// 例子：\r\n");
      out.write("\t\t// (new Date()).Format(\"yyyy-MM-dd hh:mm:ss.S\") ==> 2006-07-02 08:09:04.423\r\n");
      out.write("\t\t// (new Date()).Format(\"yyyy-M-d h:m:s.S\") ==> 2006-7-2 8:9:4.18\r\n");
      out.write("\r\n");
      out.write("\t\tDate.prototype.Format = function (fmt) { // author: meizz\r\n");
      out.write("\t\t    var o = {\r\n");
      out.write("\t\t        \"M+\": this.getMonth() + 1, // 月份\r\n");
      out.write("\t\t        \"d+\": this.getDate(), // 日\r\n");
      out.write("\t\t        \"h+\": this.getHours(), // 小时\r\n");
      out.write("\t\t        \"m+\": this.getMinutes(), // 分\r\n");
      out.write("\t\t        \"s+\": this.getSeconds(), // 秒\r\n");
      out.write("\t\t        \"q+\": Math.floor((this.getMonth() + 3) / 3), // 季度\r\n");
      out.write("\t\t        \"S\": this.getMilliseconds() // 毫秒\r\n");
      out.write("\t\t    };\r\n");
      out.write("\t\t    if (/(y+)/.test(fmt))\r\n");
      out.write("\t\t        fmt = fmt.replace(RegExp.$1, (this.getFullYear() + \"\").substr(4 - RegExp.$1.length));\r\n");
      out.write("\t\t    for (var k in o)\r\n");
      out.write("\t\t        if (new RegExp(\"(\" + k + \")\").test(fmt)) fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : ((\"00\" + o[k]).substr((\"\" + o[k]).length)));\r\n");
      out.write("\t\t            return fmt;\r\n");
      out.write("\t\t};\r\n");
